import React, { useState } from 'react';
import { Button, Form, FormGroup, Label, Input, Col, Container, Row } from 'reactstrap';

function ProjectForm() {
  const [project, setProject] = useState({
    name: '',
    features: ['', '', '']
  });

  const handleInputChange = (index, value) => {
    if (index === -1) {
      setProject({ ...project, name: value });
    } else {
      const updatedFeatures = [...project.features];
      updatedFeatures[index] = value;
      setProject({ ...project, features: updatedFeatures });
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle the submission logic here
    console.log(project);
  };

  return (
    <Container className="my-5">
      <Form onSubmit={handleSubmit}>
        <FormGroup row>
          <Label for="role" sm={2}>Select your role</Label>
          <Col sm={10}>
            <Input type="select" name="role" id="role">
              <option>Product Manager</option>
              <option>Project Manager</option>
              <option>Developer</option>
              <option>Tester</option>
            </Input>
          </Col>
        </FormGroup>
        
        <FormGroup row>
          <Label for="project-name" sm={2}>Project Name:</Label>
          <Col sm={10}>
            <Input
              type="text"
              name="projectName"
              id="project-name"
              value={project.name}
              onChange={(e) => handleInputChange(-1, e.target.value)}
            />
          </Col>
        </FormGroup>

        <FormGroup row>
          <Label for="feature-count" sm={2}>Number of Features:</Label>
          <Col sm={10}>
            <Input
              type="number"
              name="featureCount"
              id="feature-count"
              value={project.features.length}
              readOnly
            />
          </Col>
        </FormGroup>
        
        {project.features.map((feature, index) => (
          <FormGroup row key={`feature-${index}`}>
            <Label for={`feature-${index}`} sm={2}>Feature {index + 1}:</Label>
            <Col sm={10}>
              <Input
                type="text"
                name={`feature-${index}`}
                id={`feature-${index}`}
                value={feature}
                onChange={(e) => handleInputChange(index, e.target.value)}
              />
            </Col>
          </FormGroup>
        ))}
        
        <FormGroup row>
          <Col sm={{ size: 10, offset: 2 }}>
            <Button color="primary">Next</Button>
          </Col>
        </FormGroup>
      </Form>
    </Container>
  );
}

export default ProjectForm;
