import React, { useState } from 'react';

function ProjectForm() {
  const [project, setProject] = useState({
    name: '',
    features: ['', '', '']
  });

  const handleInputChange = (index, value) => {
    if (index === -1) {
      setProject({ ...project, name: value });
    } else {
      const updatedFeatures = [...project.features];
      updatedFeatures[index] = value;
      setProject({ ...project, features: updatedFeatures });
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle the submission logic here
    console.log(project);
  };

  return (
    <div className="project-form">
      <header>
        {/* Navigation goes here */}
      </header>
      <form onSubmit={handleSubmit}>
        <section className="role-selection">
          <label htmlFor="role">Select your role</label>
          <select id="role">
            <option>Product Manager</option>
            <option>Project Manager</option>
            <option>Developer</option>
            <option>Tester</option>
          </select>
        </section>
        
        <section className="project-details">
          <label htmlFor="project-name">Project Name:</label>
          <input
            id="project-name"
            type="text"
            value={project.name}
            onChange={(e) => handleInputChange(-1, e.target.value)}
          />
          
          <label htmlFor="feature-count">Number of Features:</label>
          <input
            id="feature-count"
            type="number"
            value={project.features.length}
            readOnly
          />
          
          {project.features.map((feature, index) => (
            <div key={`feature-${index}`}>
              <label htmlFor={`feature-${index}`}>Feature {index + 1}:</label>
              <input
                id={`feature-${index}`}
                type="text"
                value={feature}
                onChange={(e) => handleInputChange(index, e.target.value)}
              />
            </div>
          ))}
        </section>
        
        <button type="submit">Next</button>
      </form>
    </div>
  );
}

export default ProjectForm;
