import React, { useState } from 'react';
import { Button, Form, FormGroup, Label, Input, Col, Container } from 'reactstrap';

function ProjectForm() {
  const [projectName, setProjectName] = useState('');
  const [featureCount, setFeatureCount] = useState(3);
  const [features, setFeatures] = useState(new Array(3).fill('1'));

  const handleProjectNameChange = (e) => {
    setProjectName(e.target.value);
  };

  const handleFeatureCountChange = (e) => {
    const newFeatureCount = parseInt(e.target.value, 10);
    setFeatureCount(newFeatureCount);
    // Adjust the features array to the new count
    setFeatures(features.length < newFeatureCount ? 
      [...features, ...new Array(newFeatureCount - features.length).fill('')] : 
      features.slice(0, newFeatureCount));
  };

  const handleFeatureChange = (index, value) => {
    const updatedFeatures = [...features];
    updatedFeatures[index] = value;
    setFeatures(updatedFeatures);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle the submission logic here
    const projectData = {
      projectName,
      features
    };
    console.log(projectData);
  };

  return (
    <Container className="my-5">
      <Form onSubmit={handleSubmit}>
        <FormGroup row>
          <Label for="role" sm={2}>Select your role</Label>
          <Col sm={10}>
            <Input type="select" name="role" id="role">
              <option>Product Manager</option>
              <option>Project Manager</option>
              <option>Developer</option>
              <option>Tester</option>
            </Input>
          </Col>
        </FormGroup>
        
        <FormGroup row>
          <Label for="project-name" sm={2}>Project Name:</Label>
          <Col sm={10}>
            <Input
              type="text"
              name="projectName"
              id="project-name"
              value={projectName}
              onChange={handleProjectNameChange}
            />
          </Col>
        </FormGroup>

        <FormGroup row>
          <Label for="feature-count" sm={2}>Number of Features:</Label>
          <Col sm={10}>
            <Input
              type="number"
              name="featureCount"
              id="feature-count"
              value={featureCount}
              onChange={handleFeatureCountChange}
              min="0"
            />
          </Col>
        </FormGroup>
        
        {new Array(featureCount).fill(null).map((_, index) => (
          <FormGroup row key={`feature-${index}`}>
            <Label for={`feature-${index}`} sm={2}>Feature {index + 1}:</Label>
            <Col sm={10}>
              <Input
                type="text"
                name={`feature-${index}`}
                id={`feature-${index}`}
                value={features[index] || ''}
                onChange={(e) => handleFeatureChange(index, e.target.value)}
              />
            </Col>
          </FormGroup>
        ))}
        
        <FormGroup row>
          <Col sm={{ size: 10, offset: 2 }}>
            <Button color="primary">Next</Button>
          </Col>
        </FormGroup>
      </Form>
    </Container>
  );
}

export default ProjectForm;
